console.log('Auth fix loading...');document.addEventListener('DOMContentLoaded',function(){const loginForm=document.querySelector('form[action*="login"]');if(loginForm){console.log('Found login form,adding submit handler');loginForm.addEventListener('submit',function(e){console.log('Login form submitted');const submitBtn=this.querySelector('button[type="submit"]');if(submitBtn){const originalText=submitBtn.innerHTML;submitBtn.disabled=true;submitBtn.innerHTML='<i class="fas fa-spinner fa-spin me-2"></i>Signing in...';setTimeout(()=>{submitBtn.disabled=false;submitBtn.innerHTML=originalText},5000)}})}const removeStuckLoading=()=>{document.body.classList.remove('loading');const loadingOverlays=document.querySelectorAll('.loading-overlay,.modal-backdrop,[id*="loading"]');loadingOverlays.forEach(el=>{if(el.style.display!=='none'&&!el.classList.contains('modal')){console.log('Removing stuck loading element:',el);el.style.display='none'}});const disabledButtons=document.querySelectorAll('button[disabled],.btn[disabled]');disabledButtons.forEach(btn=>{console.log('Re-enabling button:',btn.textContent);btn.disabled=false})};removeStuckLoading();setTimeout(removeStuckLoading,500);const checkNavigation=()=>{const currentPath=window.location.pathname;console.log('Current path:',currentPath);const links=document.querySelectorAll('a[href]');links.forEach(link=>{link.addEventListener('click',function(e){console.log('Link clicked:',this.href)})})};checkNavigation()});