console.log('Loading click fix...');document.addEventListener('DOMContentLoaded',function(){console.log('Applying click fixes...');const removeBlockingBackdrops=()=>{const backdrops=document.querySelectorAll('.modal-backdrop');backdrops.forEach(backdrop=>{console.log('Removing stray backdrop:',backdrop);backdrop.remove()});const modals=document.querySelectorAll('.modal.show');modals.forEach(modal=>{if(!modal.classList.contains('d-block')){console.log('Hiding stuck modal:',modal.id);modal.classList.remove('show');modal.style.display='none'}})};const unlockBody=()=>{document.body.classList.remove('modal-open');document.body.style.overflow='';document.body.style.paddingRight=''};const checkForOverlays=()=>{const elements=document.elementsFromPoint(window.innerWidth/2,window.innerHeight/2);console.log('Elements at center of screen:',elements);elements.forEach(el=>{const computed=window.getComputedStyle(el);if(computed.pointerEvents==='none'||(computed.position==='fixed'&&computed.zIndex>1000)){console.log('Potential blocking element:',el,{pointerEvents:computed.pointerEvents,position:computed.position,zIndex:computed.zIndex})}})};const forceEnableClicks=()=>{const clickables=document.querySelectorAll('button,a,input,select,textarea,.btn,[role="button"]');clickables.forEach(el=>{el.style.pointerEvents='auto';el.style.position='relative';el.style.zIndex='10'});console.log(`Enabled clicks on ${clickables.length}elements`)};const addDebugHandler=()=>{document.addEventListener('click',function(e){console.log('Click detected on:',e.target,{tagName:e.target.tagName,className:e.target.className,id:e.target.id,href:e.target.href||'N/A'});if(e.target.tagName==='A'&&e.target.href&&!e.defaultPrevented){console.log('Link click might be blocked,attempting navigation...')}},true)};removeBlockingBackdrops();unlockBody();checkForOverlays();forceEnableClicks();addDebugHandler();setTimeout(()=>{console.log('Reapplying fixes...');removeBlockingBackdrops();unlockBody();forceEnableClicks()},1000);window.addEventListener('focus',()=>{removeBlockingBackdrops();unlockBody()});console.log('Click fixes applied!')});