class SimpleCibozerLogger{constructor(){this.logs=[];this.maxLogs=100;this.overrideConsole();console.log('Simple logger initialized')}overrideConsole(){const originalConsole={log:console.log,info:console.info,warn:console.warn,error:console.error};window.originalConsole=originalConsole}log(level,...args){const logEntry={level,timestamp:new Date().toISOString(),message:args.map(arg=>{if(typeof arg==='object'){try{return JSON.stringify(arg)}catch(e){return String(arg)}}return String(arg)}).join(' ')};this.logs.push(logEntry);if(this.logs.length>this.maxLogs){this.logs=this.logs.slice(-this.maxLogs)}}}window.CibozerLogger=new SimpleCibozerLogger();console.log('Page loaded:',document.title);window.addEventListener('error',(event)=>{console.error('JavaScript error:',event.message,'at',event.filename,':',event.lineno)});window.addEventListener('unhandledrejection',(event)=>{console.error('Unhandled promise rejection:',event.reason)});